const ui = {
    nav: 0,
}

const uiReducer = (state = ui, action) => {
    switch (action.type) {
        case 'NAV':
            return Object.assign({}, state, {
                nav: action.payload
            });
        default:
            return state;
    }
}

export default uiReducer;
