const ui = {
    nav: 0,
    sidebar: false,
    search: true,
}

const uiReducer = (state = ui, action) => {
    switch (action.type) {
        case 'UI_NAV':
            return Object.assign({}, state, {
                nav: action.payload
            });
        case 'UI_SIDEBAR':
            return Object.assign({}, state, {
                sidebar: !state.sidebar,
                search: state.search ? !state.search : state.search
            });
        case 'UI_SEARCH':
            return Object.assign({}, state, {
                sidebar: state.sidebar ? !state.sidebar : state.sidebar,
                search: !state.search
            });
        default:
            return state;
    }
}

export default uiReducer;
