const auth = {
    id: '',
    isRegister: false,
    isForgotPassword: false,
}

const authReducer = (state = auth, action) => {
    switch (action.type) {
        case 'AUTH_ID':
            return Object.assign({}, state, {
                nav: action.payload
            });
        case 'AUTH_ISREGISTER':
            return Object.assign({}, state, {
                isRegister: !state.isRegister
            });
        default:
            return state;
    }
}

export default authReducer;
