import React, { Component } from 'react';

import $ from 'jquery';
import { FiUser } from 'react-icons/fi';

import './index.css';

class Header extends Component {
	isToggle = false;
	toggleSidebar() {
		$('.sidebar').css('left', 'calc(100% - 2.5rem)');
	}

	render() {
		return (
			<div className="header">
				<div className="header-container">
					<div className="header-title">#Camagru</div>
					<FiUser className="header-icon" onClick={() => this.toggleSidebar()}/>
				</div>
			</div>
		);
	}
}

export default Header;
