const auth = {
    id: 'dlim',
    isRegister: false,
    isForgot: false,
}

const authReducer = (state = auth, action) => {
    switch (action.type) {
        case 'AUTH_ID':
            return Object.assign({}, state, {
                id: action.payload
            });
        case 'AUTH_ISREGISTER':
            return Object.assign({}, state, {
                isRegister: !state.isRegister
            });
        case 'AUTH_ISFORGOT':
            return Object.assign({}, state, {
                isForgot: !state.isForgot
            });
        default:
            return state;
    }
}

export default authReducer;
