import React, { Component } from 'react';

import $ from 'jquery';
import { FiUser } from 'react-icons/fi';

import { useSelector, useDispatch } from 'react-redux';
import { sidebar } from '../../actions';

import './index.css';

class Header extends Component {
	toggleSidebar() {
		if(useSelector(state => state.ui.sidebar)) {
			$('.sidebar').css('left', '100%');
			useDispatch(sidebar(false));
		} else {
			$('.sidebar').css('left', 'calc(100% - 12.5rem)');
			useDispatch(sidebar(false));
		}
	}

	render() {
		return (
			<div className="header">
				<div className="header-container">
					<div className="header-title">#Camagru</div>
					<FiUser className="header-icon" onClick={() => this.toggleSidebar()}/>
				</div>
			</div>
		);
	}
}

export default Header;
