import React from 'react';
import { useDispatch } from 'react-redux';
import { auth_isforgotpassword } from '../../../actions';

import FacebookLogin from 'react-facebook-login';

function ForgotPassword() {
	const dispatch = useDispatch();

	return (
		<div className='signin'>
			<div className='signin-title'>Sign up!</div>
			<form>
				<input className='signin-input' type='email' placeholder='Email Address' required />
				<button className='signin-btn' type='submit'>Find your password</button>
			</form>
			<p>Do you remember your password? <span onClick={() => dispatch(auth_isforgotpassword())}>Sign in!</span></p>
		</div>
	);
}

export default ForgotPassword;
