const auth = {
    id: ''
}

const authReducer = (state = auth, action) => {
    switch (action.type) {
        case 'AUTH_ID':
            return Object.assign({}, state, {
                nav: action.payload
            });
        default:
            return state;
    }
}

export default authReducer;
