import React from 'react';

import { useSelector, useDispatch } from 'react-redux';
import { ui_sidebar } from '../../../actions';

import { FiUser, FiX } from 'react-icons/fi';

import './index.css';

function Header() {
	const ui = useSelector(state => state.ui);
	const dispatch = useDispatch();

	return (
		<div className='header'>
			<div className='header-title'>#Camagru</div>
			{ ui.sidebar ? <FiX className='header-icon' onClick={() => dispatch(ui_sidebar())}/> : 
				<FiUser className='header-icon' onClick={() => dispatch(ui_sidebar())}/> }

		</div>
	);
}

export default Header;
